#define RELAY_PIN_9     A9     // Relay module connected to digital pin A9 (Light/Fan control)
#define RELAY_PIN_10     A10     // Relay module connected to digital pin A10 (Light/Fan control)
#define RELAY_PIN_11     A11     // Relay module connected to digital pin A11 (Light/Fan control)

