 client.publish("/smart_home/status",payload.c_str());
 }
 }


void messageReceived(String &topic,String &payload) {

 Serial.println("incoming: " + topic + ": " + payload);


 if(topic == "/smart_home/lights"){
 if(payload=="ON"){
 digitalWrite(RELAY_PIN_A8,HIGH);}
 else{digitalWrite(RELAY_PIN_A8,LOW);}
 }
 else if(topic == "/smart_home/fans"){
 if(payload=="ON"){
 digitalWrite(RELAY_PIN_A9,HIGH);}
 else{digitalWrite(RELAY_PIN_A9,HIGH);}
 }
 else{Serial.println("Unknown topic");}
 }
